function translateData(str, format) {
    if (context.global.dataMap === undefined || context.global.dataMap === null)
        context.global.dataMap = new Map();
    else 
        context.global.dataMap.clear();

    if (context.global.dataType === undefined || context.global.dataType === null)
        context.global.dataType = new Map();
    else
        context.global.dataType.clear();
    // console.log(format);
    var sumTotal = 0;
    for (var i=0;i< format.title.length;i++) {
        if (i === 0) {
            sumTotal += (format.len[i])
            if (format.type[i] === 'char') {
                context.global.dataMap.set(format.title[i],msg.payload.slice(i, sumTotal).toString('utf-8'));
            } else if (format.type[i] === 'short') {
                var buff = new Buffer(format.len[i]);
                buff = msg.payload.slice(i, sumTotal);
                var inputValue = (buff.readUInt8(1) << 8) + buff.readUInt8(0);
                context.global.dataMap.set(format.title[i], inputValue);
                delete buff;
            } else if (format.type[i] === 'int') {
                var buff = new Buffer(format.len[i]);
                buff = msg.payload.slice(i, sumTotal);
                var inputValue = (buff.readUInt8(3) << 24) + (buff.readUInt8(2) << 16) + (buff.readUInt8(1) << 8) + buff.readUInt8(0);
                context.global.dataMap.set(format.title[i], inputValue);
                delete buff;
            } 
            context.global.dataType.set(format.title[i], format.type[i]);
        } else {
            // console.log("first :" +sumTotal + " cut :" +  ((sumTotal) + format.len[i]));
            var buff = new Buffer(format.len[i]);
            buff = msg.payload.slice(sumTotal, (sumTotal) + format.len[i]);

            if (format.type[i] === 'char') {
                // console.log(format.title[i] + "/" + buff.toString('utf-8'));

                if (format.title[i] === "Length") {
                    var strTemp = format.Length;
                    context.global.dataMap.set(format.title[i], checkLengthString(strTemp, format.len[i]));
                } else if(format.title[i] === "Spare") {
                    context.global.dataMap.set(format.title[i], checkLengthString("", format.len[i]));
                } else
                    context.global.dataMap.set(format.title[i], buff.toString('utf-8'));

            } else if (format.type[i] === 'short') {
                var inputValue = (buff.readUInt8(1) << 8) + buff.readUInt8(0);
                context.global.dataMap.set(format.title[i], inputValue);
            } else if (format.type[i] === 'int') {
                var inputValue = (buff.readUInt8(3) << 24) + (buff.readUInt8(2) << 16) + (buff.readUInt8(1) << 8) + buff.readUInt8(0);
                context.global.dataMap.set(format.title[i], inputValue);               
            }
            sumTotal += format.len[i];
            context.global.dataType.set(format.title[i], format.type[i]);
            delete buff;
        }
    }
}

