function checkLengthString(str, len, type) {
    if (type === "char") {
        var check = str.length;
        if (check === undefined) {
            str = String(str);
            check = str.length;
        }
        if (check < len) {
            for (var i = 0 ; i < (len - check) ; i++) {
                str += " ";
            }
        } else if(check > len) {
            str = str.slice(0, len)
        }
        return str;
    } else {
        str = String(str);
        var check = str.length;
        var temp = "";
        if (check < len) {
            for (var i = 0 ; i < (len - check) ; i++) {
                temp += "0";
            }
            str = temp + str;
        } else if(check > len) {
            str = str.slice(0, len)
        }
        return str;
    }
}

